document.getElementById("read_button").addEventListener("click",function(){var a=new XMLHttpRequest();a.open("GET","/read");a.onload=function(){if(a.status===200){var b=a.responseText.split("@");document.getElementById("read_temperature").textContent=b[0];document.getElementById("read_humidity").textContent=b[1]}else{alert("Ошибка при чтении значения датчика: "+a.status)}};a.send()});document.getElementById("get_button").addEventListener("click",function(){var a=new XMLHttpRequest();a.open("GET","/get");a.onload=function(){if(a.status===200){var b=a.responseText.split("@");document.getElementById("get_temperature").textContent=b[0];document.getElementById("get_humidity").textContent=b[1]}else{alert("Ошибка при чтении уровней включения реле: "+a.status)}};a.send()});document.getElementById("set_button").addEventListener("click",function(){var b=document.getElementById("tempThreshold").value;var a=document.getElementById("humidityThreshold").value;var c=new XMLHttpRequest();c.open("POST","/set");c.setRequestHeader("Content-Type","application/x-www-form-urlencoded");c.onload=function(){if(c.status===200){var d=c.responseText.split("@");if(d[0]!==b||d[1]!==a){alert("Ошибка при установления нового уровня включения реле: "+c.responseText)}}else{if(c.status!==200){alert("Ошибка при установке нового уровня включения реле: "+c.status)}}};c.send(encodeURI(b)+"@"+encodeURI(a))});